import { z } from 'zod';
export declare const DurationSchema: z.ZodPipe<z.ZodUnion<readonly [z.ZodString, z.ZodNumber]>, z.ZodTransform<string, string | number>>;
/**
 * A duration of time, either as a string or a number.
 *
 * Can be provided as a **string** with units:
 * - `'10ms'` → milliseconds
 * - `'1s'`   → seconds
 * - `'1m'`   → minutes
 * - `'2h'`   → hours
 *
 * Or as a **number**, which will be treated as **milliseconds** and automatically
 * converted to a human-readable string with the appropriate unit:
 * - `500`       → `'500ms'`
 * - `1500`      → `'2s'`
 * - `75_000`    → `'1m'`
 * - `3_600_000` → `'1h'`
 */
export type Duration = z.infer<typeof DurationSchema>;
