/**
 * Camelizes a string.
 *
 * @param S - The string to camelize.
 * @returns The camelized string.
 */
export type CamelCase<S extends string> = S extends `${infer P1}_${infer P2}${infer P3}` ? `${P1}${Uppercase<P2>}${CamelCase<P3>}` : S;
/**
 * Camelizes an object.
 *
 * @param T - The object to camelize.
 * @param S - Whether to camelize the object recursively.
 * @returns The camelized object.
 */
export type CamelizeObject<T, S = false> = {
    [K in keyof T as Uncapitalize<CamelCase<string & K>>]: T[K] extends Date ? T[K] : T[K] extends RegExp ? T[K] : T[K] extends Array<infer U> ? U extends {} | undefined ? Array<CamelizeObject<U>> : T[K] : T[K] extends {} | undefined ? S extends true ? T[K] : CamelizeObject<T[K]> : T[K];
};
/**
 * Camelizes an object.
 *
 * @param T - The object to camelize.
 * @param S - Whether to camelize the object recursively.
 * @returns The camelized object.
 */
export type Camelize<T, S = false> = T extends Array<infer U> ? Array<CamelizeObject<U, S>> : CamelizeObject<T, S>;
