/**
 * Types specific to knowledge primitives (Knowledge/RAG).
 */
import type { TextEmbedderCreationConfig } from '../../common/api/library';
import { CreationConfig, KnowledgeRecord } from '../types';
/**
 * Knowledge types
 */
export interface VectorStoreConfig {
    type: 'inmemory' | 'faiss';
    dimension: number;
}
export interface KnowledgeLocalConfig {
    embedderConfig: TextEmbedderCreationConfig;
    vectorStoreConfig: VectorStoreConfig;
}
export interface KnowledgeCreationConfig extends CreationConfig<KnowledgeLocalConfig> {
}
export interface RetrievalConfig {
    topK?: number;
}
export interface GetKnowledgeRequest {
    ids?: string[];
    searchQuery?: string;
    retrievalConfig?: RetrievalConfig;
}
export interface CompileKnowledgeRequest {
    knowledgeId: string;
    records: KnowledgeRecord[];
}
export interface CompileKnowledgeFromFileRequest {
    knowledgeId: string;
    file: string;
}
export { KnowledgeRecord };
