/**
 * Types specific to NLU primitives (NER, TextClassifier, KeywordMatcher).
 */
import { EntityMatch } from '../../common/api/library';
import { ClassificationResult, KeywordMatch } from '../types';
/**
 * NER (Named Entity Recognition) types
 */
export interface DictionaryRule {
    name: string;
    displayName: string;
    synonyms: string[];
}
export interface Entity {
    name: string;
    rules: DictionaryRule[];
}
export { EntityMatch };
/**
 * TextClassifier types
 */
export interface TextClassifierConfig {
    modelWeightsPath: string;
    supportedClasses: string[];
    threshold?: number;
}
export interface ClassifyTextRequest {
    text: string;
    threshold?: number;
}
export interface ClassifiedClass {
    className: string;
    confidence: number;
}
export interface TextClassifierResponse {
    classes: ClassifiedClass[];
}
export { ClassificationResult };
/**
 * KeywordMatcher types
 */
export interface KeywordGroup {
    name: string;
    keywords: string[];
}
export interface KeywordMatchResult {
    groupName: string;
    keyword: string;
    position?: number;
}
export interface KeywordMatcherResponse {
    matches: KeywordMatchResult[];
}
export { KeywordMatch };
