/**
 * Types specific to speech primitives (STT, TTS, VAD, TurnDetector, AECFilter).
 */
import { AudioChunk } from '../../common/api/library';
import { CreationConfig, Device, LocalModelConfig, RemoteModelConfig, TurnDetectionResult } from '../types';
/**
 * STT (Speech-to-Text) types
 */
export interface STTLocalConfig extends LocalModelConfig {
    defaultConfig?: {
        languageCode?: string;
    };
}
export interface STTRemoteConfig extends RemoteModelConfig {
    defaultConfig?: {
        languageCode?: string;
    };
}
export interface STTCreationConfig extends CreationConfig<STTLocalConfig, STTRemoteConfig> {
}
export interface RecognizeSpeechConfig {
    languageCode?: string;
}
/**
 * StreamingSTT (Streaming Speech-to-Text) types
 */
export interface StreamingSTTLocalConfig {
    sttModelPath: string;
    vadModelPath: string;
    sttDevice: Device;
    vadDevice: Device;
    defaultConfig?: {
        languageCode?: string;
        silenceThresholdMs?: number;
    };
}
export interface StreamingSTTRemoteConfig extends RemoteModelConfig {
    defaultConfig?: {
        languageCode?: string;
        silenceThresholdMs?: number;
    };
}
export interface StreamingSTTCreationConfig extends CreationConfig<StreamingSTTLocalConfig, StreamingSTTRemoteConfig> {
}
export interface StreamingSTTTranscription {
    text: string;
    isFinal: boolean;
}
export type StreamingSTTSession = AsyncIterableIterator<StreamingSTTTranscription> & {
    responses: AsyncIterable<StreamingSTTTranscription>;
    getMetadata(): Record<string, any>;
};
/**
 * Stream recognition request options for StreamingSTT.
 */
export interface StreamRecognizeSpeechRequestOptions {
    /**
     * Optional streaming recognition configuration overrides.
     */
    config?: {
        languageCode?: string;
        silenceThresholdMs?: number;
    };
    /**
     * Optional credentials for remote STT providers.
     */
    credentials?: Record<string, any>;
}
/**
 * TTS (Text-to-Speech) types
 */
export interface TTSLocalConfig extends LocalModelConfig {
    defaultConfig?: {
        voice?: string;
        sampleRateHz?: number;
    };
}
export interface TTSRemoteConfig extends RemoteModelConfig {
    defaultConfig?: {
        voice?: string;
        sampleRateHz?: number;
    };
}
export interface TTSCreationConfig extends CreationConfig<TTSLocalConfig, TTSRemoteConfig> {
}
export interface SynthesizeSpeechConfig {
    voice?: string;
    sampleRateHz?: number;
}
export interface SynthesizeSpeechRequest {
    voice: string;
    text: string;
    config?: SynthesizeSpeechConfig;
}
/**
 * VAD (Voice Activity Detection) types
 */
export interface VADLocalConfig extends LocalModelConfig {
    defaultConfig?: {
        speechThreshold?: number;
    };
}
export interface VADCreationConfig extends CreationConfig<VADLocalConfig> {
}
export interface VADConfig {
    speechThreshold?: number;
}
/**
 * TurnDetector types
 */
export interface TurnDetectorLocalConfig extends LocalModelConfig {
    defaultConfig?: {
        threshold?: number;
    };
}
export interface TurnDetectorCreationConfig extends CreationConfig<TurnDetectorLocalConfig> {
}
export interface TurnDetectorConfig {
    threshold?: number;
}
export { TurnDetectionResult };
/**
 * AECFilter (Acoustic Echo Cancellation) types
 */
export interface AECFilterLocalConfig {
    filterLengthMs: number;
    device: Device;
}
export interface AECFilterCreationConfig extends CreationConfig<AECFilterLocalConfig> {
}
export interface AECFilterConfig {
    filterLengthMs?: number;
}
/**
 * Common types
 */
export { AudioChunk };
