/**
 * Common types and interfaces for primitives implementation.
 * These types are used across multiple primitive categories.
 */
import { LLMChatResponse } from '../common/api/graphs';
/**
 * Device type enumeration.
 */
export declare enum DeviceType {
    CPU = "CPU",
    CUDA = "CUDA",
    METAL = "METAL",
    OPENCL = "OPENCL"
}
/**
 * Device configuration.
 */
export interface Device {
    type: DeviceType | string;
    index: number;
}
/**
 * Device information including memory details.
 */
export interface DeviceInfo {
    name: string;
    freeMemoryBytes: number;
    totalMemoryBytes: number;
}
/**
 * Complete device descriptor with info.
 */
export interface DeviceDescriptor extends Device {
    info?: DeviceInfo;
}
/**
 * Common timeout configuration.
 */
export interface Timeout {
    seconds: number;
}
/**
 * Base configuration for local (on-device) models.
 */
export interface LocalModelConfig {
    modelPath: string;
    device: Device;
}
/**
 * Base configuration for remote (API-based) models.
 */
export interface RemoteModelConfig {
    apiKey: string;
    endpointOverride?: string;
    defaultTimeout?: Timeout;
}
/**
 * Generic creation config that can be local or remote.
 */
export interface CreationConfig<TLocal = any, TRemote = any> {
    localConfig?: TLocal;
    remoteConfig?: TRemote;
}
/**
 * LLM message role types.
 */
export declare enum MessageRole {
    System = "system",
    User = "user",
    Assistant = "assistant",
    Tool = "tool"
}
/**
 * LLM message structure.
 */
export interface Message {
    role: MessageRole | string;
    content: string;
    toolCallId?: string;
    toolCalls?: ToolCall[];
}
/**
 * Tool call structure for LLM tool execution.
 */
export interface ToolCall {
    id: string;
    name: string;
    args: string;
}
/**
 * Tool definition for LLM.
 */
export interface Tool {
    name: string;
    description: string;
    properties?: string;
}
/**
 * LLM generation result structure.
 */
export interface LLMGenerationResult extends LLMChatResponse {
}
/**
 * Keyword match result.
 */
export interface KeywordMatch {
    groupName: string;
    keyword: string;
    start: number;
    end: number;
}
/**
 * Knowledge record structure.
 */
export interface KnowledgeRecord {
    id: string;
    content: string;
    metadata?: Record<string, any>;
}
/**
 * Silence interval in audio.
 */
export interface SilenceInterval {
    start: number;
    end: number;
}
/**
 * Turn detection result.
 */
export interface TurnDetectionResult {
    prediction: number;
    probability: number;
}
/**
 * Classification result.
 */
export interface ClassificationResult {
    categories: string[];
    scores: number[];
}
